function randomStuff() {
  shapeNum = floor(random(5,42));

  for(let i = 0;  i < shapeNum; i++) {
    x = random(0, width-200);
    y = random(0, height-150);
    w = (random(10, 200));
    h = (random(10, 200));
    r = (random(-40, 80));
    shapeColR =  floor(random(0, 360));
    shapeColG =  floor(random(0, 360));
    shapeColB =  floor(random(0, 360));

    makeRect(x, y, w, h, r);
    makeCircle(x,y,w,h);
  }
}

function makeRect(x, y, w, h, r){
  fill(shapeColR,shapeColG,shapeColB);
  push();
    translate(x, y);
    rotate(r);
    rect(x, y, w, h);
  pop();
}

function makeCircle(x,y,w,h) {
  fill(shapeColR,shapeColG,shapeColB);
  ellipse(x,y,w,h);
}

