let frequency_slider;
let amplitude_slider;
let time_speed_slider;

let startX = 50;
let startY = 250;
let endX = 450;
let endY = 50;

function setup() {
  createCanvas(500, 300);

  createP("Frequency");
  frequency_slider = createSlider(0, 100, 100);
  createP("Amplitude");
  amplitude_slider = createSlider(0, 100, 50);
  createP("Time Speed");
  time_speed_slider = createSlider(0, 100, 100);
}

function draw() {
  background(50);
  ellipseMode(CENTER);

  let frequency = frequency_slider.value();
  let amplitude = amplitude_slider.value() / 10;
  let time_speed = time_speed_slider.value() / 1000

  noiseDetail(1, 0.5);
  // noiseDetail(1, 1);

  fill(255);
  noStroke();

  // study this loop. do you understand how the line of ellipses is created?
  for (i = 0; i < 1; i += 0.02) {
    let x = lerp(startX, endX, i);
    let y = lerp(startY, endY, i);

    // hint: drive offsetX and offsetY with noise() instead of random()
    // let offsetX = (random() - 0.5) * amplitude * 10;
    // let offsetY = (random() - 0.5) * amplitude * 10;

    let offsetX = (noise(i*frequency + frameCount*time_speed))*amplitude*10;
    let offsetY = (noise(i*frequency + frameCount*time_speed))*amplitude*10;

    ellipse(x + offsetX, y + offsetY, 10, 10);
  }
}

