let amplitude_slider;
let frequency_slider;
let speed_slider;

let startX = 50;
let startY = 250;
let endX = 450;
let endY = 50;

function setup() {
  createCanvas(500, 300);

  createP("Amplitude");
  amplitude_slider = createSlider(0, 100, 50);

  createP("Frequency");
  frequency_slider = createSlider(0, 10, 5, 0.1);

  createP("Speed");
  speed_slider = createSlider(0, 10, 5, 0.1);
}

function draw() {
  background(50);
  ellipseMode(CENTER);

  let amplitude = amplitude_slider.value() / 100;
  let frequency = frequency_slider.value();
  let timespeed = speed_slider.value();

  noiseDetail(1, 0.5);

  // create a color array with random colors
  let colors = [
    color(255, 0, 0),
    color(0, 255, 0),
    color(0, 0, 255),
    color(255, 255, 0),
    color(0, 255, 255),
    color(255, 0, 255)
  ];

  // study this loop. do you understand how the line of ellipses is created?
  for (i = 0; i < 1; i += 0.02) {
    let x = lerp(startX, endX, i);
    let y = lerp(startY, endY, i);

    // hint: drive offsetX and offsetY with noise() instead of random()
    let offsetX = (noise(i * frequency, frameCount * timespeed) - 0.5) * amplitude * 10;
    let offsetY = (noise(i * frequency, frameCount * timespeed) - 0.5) * amplitude * 10;

    // randomly choose a color from the array
    let col = random(colors);

    // set the fill color to the randomly chosen color
    fill(col);

    // draw the ellipse with a random size between 5 and 20
    ellipse(x + offsetX, y + offsetY, random(5, 20), random(5, 20));

    // draw a line from the previous ellipse to the current one
    stroke(col);
    strokeWeight(2);
    if (i > 0) {
      let px = lerp(startX, endX, i - 0.02);
      let py = lerp(startY, endY, i - 0.02);
      line(px + offsetX, py + offsetY, x + offsetX, y + offsetY);
    }
  }
}

