  image(img, 0, 0);

  // Get pixel values
  loadPixels();

  // Apply glitch effect
  glitchImg = createImage(img.width, img.height);
  glitchImg.loadPixels();
  for (let x = 0; x < img.width; x++) {
    for (let y = 0; y < img.height; y++) {
      let index = (x + y * img.width) * 4;
      let glitchIndex = (x + y * glitchImg.width) * 4;
      let r = pixels[index];
      let g = pixels[index + 1];
      let b = pixels[index + 2];

      // Add random offset to pixel
      glitchImg.pixels[glitchIndex] = pixels[index + int(random(-10, 10))];
      glitchImg.pixels[glitchIndex + 1] = pixels[index + 1 + int(random(-10, 10))];
      glitchImg.pixels[glitchIndex + 2] = pixels[index + 2 + int(random(-10, 10))];
      glitchImg.pixels[glitchIndex + 3] = pixels[index + 3];
    }
  }

  // Update glitch image pixels
  glitchImg.updatePixels();
  image(glitchImg, 0, 0);
}

