let dim = 400;

function setup() {
  createCanvas(dim, dim);
}

function draw() {
  // background(220);

  let col = color(81, 19, 117, 10);
  background(col);
  fill(col);
  stroke(219, 203, 31);
  strokeWeight(dim / 175);

  let num = round(dim / 25);
  let offset = 200 / num;
  let delta = 10 * cos(TWO_PI * millis() / 2000);

  for (let x = 0; x < num; x++) {
    for (let y = 0; y < num; y++) {
      let nx = noise(x) * 10;
      let ny = noise(y) * 10;

      bezier(
        0,
        ny + offset + (y * dim) / num,
        y + ny * delta,
        offset + (y * dim) / num - delta * ny,
        y + ny * delta * 10,
        offset + (y * dim) / num + delta * ny,
        dim,
        ny + offset + (y * dim) / num
      );
      bezier(
        nx + offset + (x * dim) / num,
        0,
        offset + (x * dim) / num - delta * nx,
        x + nx * delta,
        offset + (x * dim) / num + delta * nx,
        x + nx * delta * 10,
        nx + offset + (x * dim) / num,
        dim
      );

    }
  }

}

