// require https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js

let amplitude_slider;
let frequency_slider;
let timespeed_slider;

let startX = 50;
let startY = 250;
let endX = 450;
let endY = 50;

function setup() {
  createCanvas(500, 300);

  createP("Amplitude");
  amplitude_slider = createSlider(0, 100, 0);
  createP("Frequency");
  frequency_slider = createSlider(0, 100, 0);
  createP("Time Speed");
  timespeed_slider = createSlider(0, 100, 0);

}

function draw() {
  background(50);
  ellipseMode(CENTER);

  let amplitude = amplitude_slider.value() / 100;
  let frequency = frequency_slider.value() / 100;
  let timespeed = timespeed_slider.value();

  for (i = 0; i < 1; i += 0.02) {
    let x = lerp(startX, endX, i);
    let y = lerp(startY, endY, i);

    //offset with amplitude
    let aX = (noise(amplitude)-0.5)*100;
    let aY = (noise(amplitude)-0.5)*100;

    //offset with frequency
    let fX = noise(i*frequency*10)*amplitude*200
    let fY = noise(i*frequency*10)*amplitude*200

    //combine amplitude and frequency offsets
    let xPos = x+aX+fX;
    let yPos = y+aY+fY;

    //animate with noise as a function of time, controlled by amplitude value
    let tX_offset = timespeed * sin(i * noise(x*(millis()%(amplitude+1))));
    let tY_offset = timespeed * sin(i * noise(y*(millis()%(amplitude+1))));
    let tX = map(tX_offset,0,20,0,5)
    let tY = map(tY_offset,0,20,0,5)
    xPos += tX;
    yPos += tY; 

    //draw ellipse
    fill(255);
    noStroke();
    ellipse(xPos , yPos , 10, 10);
  }
}

