  for (let i = 0; i < 36; i++) {
    myTurtle.moveForward(length);
    myTurtle.turnLeft(70);
  }

  myTurtle.popState();

  let reduce = 0.72;
  drawSpiro(length*reduce, x*reduce, y*reduce);

}

