// const cminor = ["C3", "D3", "Eb3", "F3", "G3", "Ab3", "B3"];
const notesnew = [
  "C3", "D3", "E3", "F3", "G3", "A3", "B3", 
  "C4", "D4", "E4", "F4", "G4", "A4", "B4"
];
const notes = notesnew;

let degree = 0;

function generate() {
  degree = randomInt(0, 7);

  const a = generateMeasure();
  const b = generateMeasure();
  const c = generateMeasure();

  b[b.length - 1][0] = notes[4];
  c[c.length - 1][0] = notes[0];

  return [].concat(a, b, a, c);
}
function generateMeasure() {
  const m = [];

  let timeLeft = Tone.Time("1m");

  while (timeLeft.toSeconds() > 0) {

    let change = sample([-1, -1, -1, 1, 1, -2, 2, 2, 2, -3]); 
    degree = constrain(degree + change, 0, notes.length - 1);
    const note = notes[degree];

    let length = Tone.Time("4n");
    // let r = Math.random();
    if (Math.random() < 0.5) {
        length = Tone.Time("2n");
    }
    // if (r > 0.3333 && r < 0.6666) {
    //   length = Tone.Time("2n");
    // } else if (r > 0.6666) {
    //   length = Tone.Time("8n");
    // }
    if (length.toMilliseconds() > timeLeft.toMilliseconds()) {
      length = timeLeft;
    }
    timeLeft = Tone.Time(timeLeft - length);

    m.push([note, length, degree]);

  }

  return m;
}

function play(melody) {
  let t = Tone.now();
  for (const note of melody) {
    console.log(note[0], note[1].toNotation());
    if (note[0] !== "rest") {
      synth.triggerAttackRelease(note[0], Tone.Time(note[1]) - 0.1, t);
      if (note[2] < notes.length - 3 && Math.random() < 0.5) {
          synth.triggerAttackRelease(notes[note[2] + 2], Tone.Time(note[1]) - 0.1, t);
      }
      if (note[2] < notes.length - 5 && Math.random() < 0.3) {
          synth.triggerAttackRelease(notes[note[2] + 4], Tone.Time(note[1]) - 0.1, t);
      }
    }
    t += Tone.Time(note[1]);
  }
}


function randomInt(min, max) {
  return Math.floor(Math.random() * (max - min) + min);
}
function sample(data) {
  const index = Math.floor(Math.random(data.length));
  return data[index];
}
function constrain(v, min, max) {
  return Math.min(max, Math.max(min, v));
}

