let skew;
let points = [];
let increase;

function setup() {
  // noLoop();
  // frameRate(10)
  createCanvas(windowWidth, windowHeight);

  skew = false;
  increase = false;
}

function draw() {

  background(40);

  if(!skew){
    points = [min(random(0,60), random(0,60), random(0,60), random(0,60), random(0,60)), max(random(10,100), random(10,100), random(10,100), random(10,100), random(10,100)), (random(150,200), random(150,200), random(150,200), random(150,200), random(150,200))*0.2, random(windowWidth/2,windowHeight)];
    point1 = random(points);
    point2 = random(points);
    point3 = random(points);
    point4 = random(points);
    skew = true;
  }
  else {
    if(point1<100 && !increase){
    point1++;
    point2++;
    point3++;
    point4++;
    }
    else{
    point1--;
    point2--;
    point3--;
    point4--;
    increase = true;
    }
  }
  if(point1<0 && point2<0 && point3<0 && point4){
    if(increase){
    skew = false;
      increase = false;
    }
  }

  noFill();
  for(let i=0; i<100; i++){
  shapeDrawing(point1, point2, point3, point4, i);
  shapeDrawing(point2, point3, point4, point1, i+10);
  shapeDrawing(point3, point4, point1, point2, i+20);
  shapeDrawing(point4, point1, point2, point3, i+30);
  }

}

function shapeDrawing(p1, p2, p3, p4, r){

  R = 50+p1;
  G = 100+p4;
  B = 80 + p3;
  A = 150 + p2;

  stroke(R, G, B, A)

  beginShape();
      vertex(width/2 - r - p1, height/2 + r + p2);
      vertex(width/2 - r - p2, height/2 - r - p3);
      vertex(width/2 + r + p3, height/2 - r - p4);
      vertex(width/2 + r + p4, height/2 + r + p1);
      endShape(CLOSE);
}

