let amplitude_slider;
let timeSpeed_slider;
let frequency_slider;

let startX = 0;
let endX = 540;

let amplitude, frequency, timeSpeed;

let fromCol ;
let toCol ;

function setup() {
  createCanvas(500, 320);
  stroke(240);
  strokeWeight(0.2);
  //noStroke();
  push();
  fill("white");
  createP("Amplitude");
  amplitude_slider = createSlider(10, 400, 100);
  createP("Frequency");
  frequency_slider = createSlider(0, 100, 30);
  createP("TimeSpeed");
  timeSpeed_slider = createSlider(0, 50, 30);
  pop();

  fromCol = color(floor(random(255)), floor(random(255)), floor(random(255)));
  toCol = color(floor(random(255)), floor(random(255)), floor(random(255)));
}

function draw() {
  background(240);
  ellipseMode(CENTER);

  amplitude = amplitude_slider.value() / 2;
  frequency = frequency_slider.value() / 3;
  timeSpeed = timeSpeed_slider.value() / 15000;

  noiseDetail(1, 0.5);

  //noStroke();

  for (
    let start = 0, end = -10, n1 = 3.4, n2 = 5.4;
    start < 361;
    start += 12, end += 12, n1 -= 0.3, n2 -= 0.3
  ) {
    drawLine(start, end, n1, n2);
  }
}

function drawLine(startY_, endY_, noiseX, noiseY) {
  for (let i = 0; i < 1; i += 0.02) {
    let x = lerp(startX, endX, i);
    let y = lerp(startY_, endY_, i);

    let nX = noise(i * frequency + millis() * timeSpeed, noiseX);
    let nY = noise(i * frequency + millis() * timeSpeed, noiseY);

    let offsetX = (nX - 0.5) * amplitude;
    let offsetY = (nY - 0.5) * amplitude;

    let finCol = lerpColor(fromCol, toCol, (nX+nY)/2);
    fill(finCol);

    rect(x + offsetX, y + offsetY, 12, 12);
    rect(x - offsetX, y - offsetY, 12, 12, 2,2,2,2);
  }
}

function mouseClicked() {
  if (mouseX > 0 && mouseX < 500 && mouseY > 0 && mouseY < 320) {
    fromCol = color(floor(random(255)), 40, floor(random(255)));
    toCol = color(floor(random(255)), 200, floor(random(255)));
  }
}

