let rectWidth = gridSizeX;
let rectHeight = gridSizeY;
let slopX;

function setup() {
  createCanvas(1000, 1000);
  background("black");
  noLoop();
}

function draw() {
  background(250);

  // fill(0, 0, 0, 100);

  for (x=gridSizeX; x< width - gridSizeX; x+=gridSizeX) {

    // print(noise(x*10)*10);
    // slopX = noise(x)*100;
    // slopX = noise(x)*20;
    slopX = x;
    slopX = x/2;
    slopX = x/20;

    // slopX = mouseX/50;

    for (y=gridSizeY; y< height - gridSizeY; y+=gridSizeY) {

      fill(x/5+10, y/5, x, 0 + x/6);
      fill(y/5+10, x/5, x, 0 + x/6);
      fill(x/4, x/6, x/4, 0 + x/5);

      fill(x/10, y/8, x/10, 0 + x/4);
      // fill(x, y/5, 0, noise(y+x)*30);
      // stroke(y/10, 0, 0, 0 + x/6);
      // stroke(y/10, 0, 0, 0 + x/6);

  // fill(0, 0, 0, random(100));
      sloppyRect(x, y, rectWidth, rectHeight, slopX);
      fill(x/2+10, y/2, y/5, 0 + x/6);
      sloppyRect(x, y, rectWidth, rectHeight, slopX);
      fill(y/3+10, x/5, x/3, 0 + x/6);
      sloppyRect(x, y, rectWidth, rectHeight, slopX);
    }
  }
}

function sloppyRect(x, y, w, h, slop) {
  beginShape();
  vertex(
    x + random(-slop, slop), //
    y + random(-slop, slop)
  );
  vertex(
    x + w + random(-slop, slop), //
    y + random(-slop, slop)
  );
  vertex(
    x + w + random(-slop, slop), //
    y + h + random(-slop, slop)
  );
  vertex(
    x + random(-slop, slop), //
    y + h + random(-slop, slop)
  );
  endShape(CLOSE);
}

