class Line {
  constructor() {
    this.pos = createVector(random(width), random(height));
    this.angle = random(TWO_PI);
    this.speed = random(1, 3);
    this.alpha = random(50, 200);
  }

  update(target, noiseVal) {
    //calculate the direction towards the target
    let dir = p5.Vector.sub(target, this.pos);
    dir.normalize();

    //update the angle of the line based on the direction towards the target and some noise
    this.angle = atan2(dir.y, dir.x) + noiseVal * TWO_PI * 0.5 - TWO_PI * 0.25;

    //move the line in the direction of the angle at a speed proportional to the distance to the target
    let distance = p5.Vector.dist(target, this.pos);
    this.pos.add(p5.Vector.fromAngle(this.angle).mult(this.speed * distance / 100));

    //transparency
    this.alpha -= 0.5;
    if (this.alpha < 0) {
      this.alpha = 0;
    }
  }

  draw(lengthFactor, lineCol) {
    stroke(lineCol, this.alpha);
    strokeWeight(1);
    line(
      this.pos.x, this.pos.y, 
      this.pos.x + cos(this.angle) * lengthFactor, 
      this.pos.y + sin(this.angle) * lengthFactor
    );
  }
}

