//haven't changed these - can be replaced
const fmajor = ["F3", "G3", "A3", "Bb3", "C3", "D3", "E3"];
const cminor = ["C3", "D3", "Eb3", "F3", "G3", "Ab3", "B3"];
const notes1 = fmajor;
const notes2 = cminor;

let degree = 0;

//generate function is the same
function generate() {
  degree = randomInt(0, 7);

  const a = generateMeasure();
  const b = generateMeasure();
  const c = generateMeasure();

  // last note should be the tonic
  c[c.length - 1][0] = notes1[0];

  return [].concat(a, c, a, b);
}

function generateMeasure() {
  const m = [];
  const n = [];

  let timeLeft = Tone.Time("1m"); //

  while (timeLeft.toSeconds() > 0) {
    //changed sample
    const change = sample([-1, -1, 1, 1, 1, 2, 2, 2, 3, 4, -3]);
    degree = constrain(degree + change, 0, 8);
    const note1 = notes1[degree];
    const note2 = notes2[degree];

    // increased length
    let length = Tone.Time("8n"); // time in a sequence

    if (Math.random() < 0.7) {
      //increased tonetime
      length = Tone.Time("4n");
    }
    if (length.toMilliseconds() > timeLeft.toMilliseconds()) {
      length = timeLeft;
    }

    timeLeft = Tone.Time(timeLeft - length);

    m.push([note1, length]);
    n.push([note2, length]);
  }

  return m, n;
}

function play(melody) {
  let t = Tone.now();
  for (const note of melody) {
    if (note[0] !== "rest") {
      //added bass and cymbal sounds here
      cymbalSynth.triggerAttackRelease(Tone.Time(note[1]) - 0.1, t);
    }
    if (note[0] !== "rest") {
      bassSynth.triggerAttackRelease(note[0], note[1], t);
    }
    t += Tone.Time(note[1]);
  }
}

