function setup() {
  createCanvas(600, 600);

  testImage.loadPixels();
  noSmooth();
}

function draw() {
  background(0,0,0);

  testImage.loadPixels(); 
  let redAvg 
  let greenAvg
  let blueAvg

  let redAtPoint
  let greenAtPoint
  let blueAtPoint  

  let pixelColor

  for (let startPointX = 0; startPointX < 20; startPointX++) {
    for (let startPointY = 0; startPointY < 20; startPointY++) {
      redAvg = 0
      greenAvg = 0
      blueAvg = 0

      for (let x = 1 + (20*startPointX); x < (20*(startPointX+1)); x++) {
        for (let y = 1 + (20*startPointY); y < (20*(startPointY+1)); y++) {
          pixelColor = getQuick(testImage, x, y);

          redAtPoint = pixelColor[0];
          greenAtPoint = pixelColor[1];
          blueAtPoint = pixelColor[2];

          redAvg = redAvg + redAtPoint;
          greenAvg = greenAvg + greenAtPoint;
          blueAvg = blueAvg + blueAtPoint;

        }

      }

      redAvg = round(redAvg / pow(19,2));
      greenAvg = round(greenAvg / pow(19,2));
      blueAvg = round(blueAvg / pow(19,2));

      extraRed = (abs(127-redAvg)*7) + redAvg;
      extraGreen = (abs(127-greenAvg)*7) + greenAvg;
      extraBlue = (abs(127-blueAvg)*7) + blueAvg;

      for (let x = 1 + (20*startPointX); x < (20*(startPointX+1)); x++) {
        for (let y = 1 + (20*startPointY); y < (20*(startPointY+1)); y++) {
          setQuick(testImage, x, y, [extraRed, extraGreen, extraBlue, 255])
        }
      }

    }
  }

  testImage.updatePixels();

  image(testImage, 0, 0, 600, 600);

}

