function draw() {
  background(245, 246, 251);

  let triLength = (width - 2 * border) / num;
  let colour = [color(94, 172, 211), color(253, 85, 14)];

  translate(width / 2, height / 2);
  translate(triLength, triLength);

  for (let i = (-1 * num) / 4; i < num / 4; i++) {
    for (let j = (-1 * num * dim) / 4; j < (num * dim) / 4; j++) {
      let triX = i * 2 * triLength;
      let triY = j * 2 * triLength;

      fill(random(colour));
      triangle(
        triX,
        triY,
        triX - triLength,
        triY - triLength,
        triX - triLength,
        triY + triLength
      );

      fill(random(colour));
      triangle(
        triX,
        triY,
        triX - triLength,
        triY - triLength,
        triX + triLength,
        triY - triLength
      );

      fill(random(colour));
      triangle(
        triX,
        triY,
        triX + triLength,
        triY - triLength,
        triX + triLength,
        triY + triLength
      );

      fill(random(colour));
      triangle(
        triX,
        triY,
        triX + triLength,
        triY + triLength,
        triX - triLength,
        triY + triLength
      );
    }
  }
}

