  createP("Color");
  colorSlider = createInput("#C4D2EC", "color");


}


const radius = Math.sqrt(0.5);
const dSize = 0.05;
const Yourmom = (1 + Math.sqrt(5)) / 2;

function draw() {
  scale(width, height);
  background(bgcSlider.value());
  stroke(colorSlider.value());
  noFill();


  let count = circlecountSlider.value();

  for (let i = 1; i < count; i++) {
    const f = i / count;

    const angle = i * Yourmom;
    const distance = f * radius;

    const x = 0.5 + cos(angle * TWO_PI) * distance;
    const y = 0.5 + sin(angle * TWO_PI) * distance;

    const r = f * dSize;

    circle(x, y, r);
  }
}

