let boxes = [];
let boxMaterials = [];
let boxMeshes = [];
let boxRows = 14;

let max = 2.5;
let offset = max/6.25;
let squareSize = max/10;
let squareHeightBase = max*1.6;
let sinOffset = (max*1.2) / 4.5;

for (let i=-1*max; i<max+offset; i+=offset) {
    for (let j=-1*max; j<max+offset; j+=offset) {
        boxes[i + j*boxRows] = new THREE.BoxGeometry(
            squareSize, squareSize, 
            squareHeightBase + Math.sin(i*sinOffset) + Math.sin(j*sinOffset)
        );
        boxMaterials[i + j*boxRows] = new THREE.MeshLambertMaterial(
            {color: "hsl(" +  Math.abs(((i*j)%15) + 10 ) + ", 80%, 50%)"}
        );
        boxMeshes[i + j*boxRows] = new THREE.Mesh(boxes[i + j*boxRows], boxMaterials[i + j*boxRows]);
        boxMeshes[i + j*boxRows].position.set(i,j,0);
        scene.add(boxMeshes[i + j*boxRows]);
    }  
}

let spheres = [];
let sphereMaterials = [];
let sphereMeshes = [];
let sphereRows = 50;
let sphereoffset = 1;

for (let i=-1*max; i<max+sphereoffset; i+=sphereoffset) {
    for (let j=-1*max; j<max+sphereoffset; j+=sphereoffset) {
        for (let k=0; k<5; k+=sphereoffset) {
            spheres[i + j*sphereRows] = new THREE.SphereGeometry((Math.random()*0.075), 25, 25);
            sphereMaterials[i + j*sphereRows] = new THREE.MeshLambertMaterial({color: 0xFFFFFF});
            sphereMeshes[i + j*sphereRows] = new THREE.Mesh(spheres[i + j*sphereRows], sphereMaterials[i + j*sphereRows]);
            sphereMeshes[i + j*sphereRows].position.set(
                (Math.random()*2*max)-max,
                (Math.random()*2*max)-max,
                (Math.random()*3.25)
            );
            scene.add(sphereMeshes[i + j*sphereRows]);
        }
    }  
}

var light = new THREE.PointLight(0xFFFFFF, 1, 500);
light.position.set(-4,-4,18);
scene.add(light);

let boxTheta = 0;
let sphereTheta = 0;

var render = function(){
    requestAnimationFrame(render);

    for (let i=-1*max; i<max+offset; i+=offset) {
        for (let j=-1*max; j<max+offset; j+=offset) {
            boxMeshes[i + j*boxRows].rotation.z += 0.05;
            boxMeshes[i + j*boxRows].position.z += (Math.sin(boxTheta + i + j))/20;
            boxTheta += 0.001;
        }
    }

    for (let i=-1*max; i<max+sphereoffset; i+=sphereoffset) {
        for (let j=-1*max; j<max+sphereoffset; j+=sphereoffset) {
            sphereMeshes[i + j*sphereRows].position.x += (Math.sin(sphereTheta + i + j))/100;
            sphereMeshes[i + j*sphereRows].position.y += (Math.sin(sphereTheta + i + j))/100;
            sphereMeshes[i + j*sphereRows].position.z += (Math.sin(sphereTheta + i + j))/100;            
            sphereTheta += 0.001;
        }
    }

    renderer.render(scene,camera);
}
render();

