function drawShapes() {
  squareSize = squaresizeSlider.value();
  circleSize = circlesizeSlider.value();

  rect(0, 0, squareSize, squareSize);
  ellipse(0, 0, circleSize, circleSize);

  for(y = 0; y < height; y++) {
    for(x = 0; x < width; x++) {
      translate(x + 10, y + 20);
      rect(x * 10 + 5, y * 10 + 5, squareSize, squareSize);

      ellipse(x * 5, y + 40, circleSize, circleSize);

      rect(x, y + 100, squareSize, squareSize);

      ellipse(x / 10, y + 200, circleSize, circleSize);

      ellipse(x * 20 + 5, y, circleSize, circleSize);
    }
  }
}

