let num = 10;
let eMax = 30;
let eMin = 5;

function setup() {
  createCanvas(400, 400);
  angleMode(DEGREES);
  noStroke();
  frameRate(3);
  colorMode(HSB)
}

function draw() {
  background(220);

  for (let i = 2 * num; i < width; i += 4 * num) {
    for (let j = 2 * num; j < height; j += 4 * num) {

      let inner;
      let outer;
      let d = dist(i, j, mouseX, mouseY);
      let colBrt = map(d, 0, width, 100, 10);
      let colHue = map(d, 0, width, 250,350);

      if (d < width / 4) {
        inner = min(
          random(eMin),
          random(eMin),
          random(eMin),
          random(eMin),
          random(eMin)
        );
        outer = min(
          random(eMax),
          random(eMax),
          random(eMax),
          random(eMax),
          random(eMax)
        );
      } else if (d < width / 2) {
        inner =
          (random(eMin) +
            random(eMin) +
            random(eMin) +
            random(eMin) +
            random(eMin)) /
          5;
        outer =
          (random(eMax) +
            random(eMax) +
            random(eMax) +
            random(eMax) +
            random(eMax)) /
          5;
      } else {
        inner = max(
          random(eMin),
          random(eMin),
          random(eMin),
          random(eMin),
          random(eMin)
        );
        outer = max(
          random(eMax),
          random(eMax),
          random(eMax),
          random(eMax),
          random(eMax)
        );
      }

      push();

      translate(i, j);

      let petalNum = floor(random(8,25));
      for (let a = 0; a < petalNum; a++) {
        rotate(a * (360 / petalNum));
        fill(colHue, colBrt, 100);
        beginShape();
        curveVertex(0, 0);
        curveVertex(inner, 0);
        curveVertex(outer + inner, outer - inner);
        curveVertex(outer + inner, outer + inner);
        curveVertex(outer - inner, outer + inner);
        curveVertex(0, inner);
        curveVertex(0, 0);
        endShape();
      }
      fill(colHue, colBrt, 50);
      ellipse(0, 0, 2*inner, 2*inner);

      pop();

    }
  }

}

using System.Collections;
using System.Collections.Generic;
using UnityEngine;
using System.Diagnostics;

public class PlaceCubes : MonoBehaviour
{
    public int period = 1000;
    public GameObject prefab;
    float prevMs = 0f;

    Stopwatch sw = new Stopwatch();


    // Start is called before the first frame update
    void Start()
    {
        sw.Start();
    }

    // Update is called once per frame
    void Update()
    {
        float currentMs = sw.ElapsedMilliseconds;
        if (currentMs - prevMs > period)
        {
            var position = new Vector3(Random.Range(-20.0f, 20.0f), Random.Range(-20.0f, 20.0f), 0);
            Instantiate(prefab, position, Quaternion.identity);
            prevMs = currentMs;
        }

    }
}

