function drawPlanes() {
  for(i = 0; i < width; i += 10) {
    for(x = 10; x < width; x = x + 10) {
      for(y = 10; y < height; y = y + 10){
        n = noise(i * y);
        fc = frameCount / 2;

        rotateY(fc / 2);

        ambientMaterial(255, 0, 0);

        push();
        translate(x * noise(x), y * noise(y), 0);
        plane(150 * n);
        pop();

        ambientMaterial(200, 50, 50);

        push();
        translate(x * noise(x) / 2, y * noise(y) / 2, 0);
        plane(150 * n);
        pop();
      }
    }
  }
}

