  var point = new Point(Math.random() * view.size.width, Math.random() * view.size.height);
  var rectangle = new Rectangle(point, size);

  // Choose a random color from the array of colors
  var color = colors[Math.floor(Math.random() * colors.length)];

  // Create a new Path object and draw an ellipse with the chosen color
  var path = new Path.Ellipse(rectangle);
  path.strokeColor = color;

  // Rotate the ellipse
  path.rotate(i * 10);

  // Scale the stroke width of the ellipse based on its size
  path.strokeWidth = size.width / 50;
}

