/**
* Animated type using loadPixels() method to get font area
*
* MOUSE
* position x/y             : affect randomness
*
* KEYS
* A-Z                      : type letters
* Arrow left/right         : toggle through draw modes
* Arrow up/down            : increase/decrease point density
* CONTROL                  : save png
*
* CONTRIBUTED BY
* [Joey Lee](http://jk-lee.com)
*/
'use strict';

let cw = window.innerWidth;
let ch = window.innerHeight;

var font;

var textTyped = 'COMP FORM';
var drawMode = 1;
var fontSize = 250;
var padding = 10;
var nOff = 0;
var pointDensity = 5;

var paths;
var textImg;

function preload() {
  font = loadFont('assets/ABCGravity-ExtraCondensed-Trial.otf');
}

function setup() {
  createCanvas(cw, ch);
  rectMode(CENTER);

  pixelDensity(1);

  setupText();
}

function setupText() {
  // create an offscreen graphics object to draw the text into
  textImg = createGraphics(width, height);
  textImg.pixelDensity(1);
  textImg.background(255);
  textImg.textFont(font);
  textImg.textSize(fontSize);
  textImg.text(textTyped, 100, fontSize + 50);
  textImg.loadPixels();
}

function draw() {
  background(0);

  nOff++;

  for (var x = 0; x < textImg.width; x += pointDensity) {
    for (var y = 0; y < textImg.height; y += pointDensity) {
      // Calculate the index for the pixels array from x and y
      var index = (x + y * textImg.width) * 4;
      // Get the red value from image
      var r = textImg.pixels[index];

      if (r < 128) {

        if (drawMode == 1){
          strokeWeight(.5);

          var noiseFac = map(mouseX, 0, width, 0, .3);
          var heightFac = 1;
          var widthFac = 20;
          var num = noise((x + nOff) * noiseFac, y * noiseFac);

          var num = noise((x + nOff) * noiseFac, y * noiseFac);
          if (num < 0.6) {
            fill(color(255,100,200))
            stroke(color(255,255,255));
          } else if (num < 0.7) {
            stroke(color(255,255,255));
          } else {
            fill(color(200,200,100))
            stroke(color(255,255,255));
          }

          push();
          translate(x, y);
          rotate(radians(30));
          ellipse(0, 0, 12*widthFac, 12*heightFac);
          pop();
        }
      }
    }
  }
}

function keyTyped() {
  if (keyCode >= 32){
    textTyped += key;
    setupText();
  }
}

// require https://cdn.jsdelivr.net/npm/p5@latest/lib/p5.min.js

let cubePos, spherePos, conePos, torusPos;
let cubeSize = 50;

function setup() {
  createCanvas(400, 400, WEBGL);

  // initialize positions
  cubePos = createVector(-150, 0, 0);
  spherePos = createVector(-50, 0, 0);
  conePos = createVector(50, 0, 0);
  torusPos = createVector(150, 0, 0);
}

function draw() {
  background(220);
  rotateX(frameCount * 0.01);
  rotateY(frameCount * 0.01);

  // draw cubes
  for (let i = -1; i <= 1; i++) {
    for (let j = -1; j <= 1; j++) {
      push();
      translate(cubePos.x + i * 150, cubePos.y + j * 150, cubePos.z);
      box(cubeSize);
      pop();
    }
  }

  // draw spheres
  for (let i = -1; i <= 1; i++) {
    for (let j = -1; j <= 1; j++) {
      push();
      translate(spherePos.x + i * 150, spherePos.y + j * 150, spherePos.z);
      sphere(50);
      pop();
    }
  }

  // draw cones
  for (let i = -1; i <= 1; i++) {
    for (let j = -1; j <= 1; j++) {
      push();
      translate(conePos.x + i * 150, conePos.y + j * 150, conePos.z);
      cone(50, 100);
      pop();
    }
  }

  // draw tori
  for (let i = -1; i <= 1; i++) {
    for (let j = -1; j <= 1; j++) {
      push();
      translate(torusPos.x + i * 150, torusPos.y + j * 150, torusPos.z);
      torus(50, 10);
      pop();
    }
  }
}

function keyPressed() {
  if (keyCode === LEFT_ARROW) {
    cubePos.x -= 10;
    spherePos.x -= 10;
    conePos.x -= 10;
    torusPos.x -= 10;
  } else if (keyCode === RIGHT_ARROW) {
    cubePos.x += 10;
    spherePos.x += 10;
    conePos.x += 10;
    torusPos.x += 10;
  } else if (keyCode === UP_ARROW) {
    cubePos.y -= 10;
    spherePos.y -= 10;
    conePos.y -= 10;
    torusPos.y -= 10;
  } else if (keyCode === DOWN_ARROW) {
    cubePos.y += 10;
    spherePos.y += 10;
    conePos.y += 10;
    torusPos.y += 10;
  } else if (key === 'w') {
    cubePos.z -= 10;
    spherePos.z -= 10;
    conePos.z -= 10;
    torusPos.z -= 10;
  } else if (key === 's') {
    cubePos.z += 10;
    spherePos.z += 10;
    conePos.z += 10;
  }
}

