let myTurtle;

function setup() {
  createCanvas(500, 500);
  myTurtle = new Turtle();
}

function draw() {
  background(50);

  noFill();
  stroke(255, 0, 0, 200); // red stroke color with 200 alpha
  strokeWeight(4); // thicker stroke weight

  // move to starting position (without drawing)
  myTurtle.penUp();
  myTurtle.moveTo(250, 250); // start at center of canvas

  // put the pen down to draw
  myTurtle.penDown();

  // draw the hexagon
  for (let i = 0; i < 6; i++) {
    myTurtle.moveForward(100);
    myTurtle.turnRight(60);
  }

  // draw the circles
  myTurtle.penUp(); // lift the pen to move to the center of the hexagon
  myTurtle.moveTo(250, 250);
  myTurtle.penDown();
  circle(250, 250, 50); // draw a circle with radius 50 at the center of the hexagon
  circle(250, 250, 80); // draw a larger circle with radius 80 at the center of the hexagon

  noLoop();
}

