function setup() {
  createCanvas(canvasWidth, canvasHeight);
  colorMode(HSB);

  video = createCapture(VIDEO);
  video.size(width, height);
  poseNet = ml5.poseNet(video, {multiplier:0.5}, modelReady);
  poseNet.on('pose', function(results) {
    poses = results;
  });
}

function modelReady() {
  select('#status').html('Model Loaded');
}

function draw() {
  background(250, 50, 85);

  let imgsize = 80;
  image(video, 0, 0, imgsize*1.2, imgsize);

  let xFactor;
  let mapFactor;  
  for (let i = 0; i < poses.length; i++) {
    let skeleton = poses[i].skeleton;
    for (let j = 0; j < skeleton.length; j++) {
      mapFactor = skeleton[0][1]
      xFactor = map(mapFactor.position.x,0,width,100,700)
    }
  }

  ellipse0.xPos = xFactor;

  fill(0, 0, 100);
  noStroke();
  ellipse(xFactor, width/2, ellipse0.esize);

  for (let i=0; i<string.length; i++) {

    amplitude = (vibration/2) * sin(TWO_PI * (millis()/1000)) * string[i].deceleration;

    noFill();
    stroke(0, 0, 100);
    strokeWeight(1.75);
    bezier(
      string[i].stringX, string[i].stringYoffset,
      amplitude + string[i].stringX, string[i].stringYoffset + string[i].bezierYoffset, 
      amplitude + string[i].stringX, height - string[i].stringYoffset - string[i].bezierYoffset, 
      string[i].stringX, height - string[i].stringYoffset
    );

    string[i].deceleration -= 0.01;
    if (string[i].deceleration < 0) {
      string[i].deceleration = 0;
    }

    if (
        ellipse0.xPos > string[i].stringX - 5 && 
        ellipse0.xPos < string[i].stringX + 5 //&& 
    ) {
      synth.triggerAttackRelease(string[i].note, "4n");
      string[i].deceleration = 1;
    }

  }

}

