const radius = Math.sqrt(0.5);
const Size = 0.1;
const variable = (1 + Math.sqrt(5)) / 2;

function draw() {
  scale(width, height);
  background(r_Slider.value(), g_Slider.value(), b_Slider.value());

  let count = circlecountSlider.value();

  for (let i = 1; i < count; i++) {
    const f = i / count * noise(i);

    const angle = i * variable + noise(i);
    const distance = f * radius;

    const x = 0.5 + cos(angle * PI) * distance * noise(i);
    const y = 0.5 + sin(angle * TWO_PI) * distance;

    const r = f * Size;

    fill(225, 240, 250, 50 * noise(distance));
    circle(x, y, r);
  }
}

