function randomizePos(z) {
  let a = random();
  if (a < 0.33 && frameCount != 0) {
    z -= height / Fscale.length;
  } else if (a < 0.66) {
    z += height / Fscale.length;
  }
  return z;
}

// Define tree module
module tree(height, radius, angle, iteration) {
  if (iteration > 0) {
    cylinder(h=height, r1=radius, r2=radius * 0.7, center=true);
    translate([0, 0, height]) {
      rotate([0, angle, 0]) {
        scale(0.7) tree(height * 0.7, radius * 0.7, angle, iteration - 1);
      }
      rotate([0, -angle, 0]) {
        scale(0.7) tree(height * 0.7, radius * 0.7, angle, iteration - 1);
      }
    }
  } else {
    sphere(r=height/5);
  }
}

