class Square {
  constructor(x, y, imgWidth, imgHeight) {
    this.x = x;
    this.y = y;
    this.color = noise(x*.3,y *.3)*360;
    this.sideLength = 20;
    this.width = imgWidth;
    this.height = imgHeight;

  }
  draw() {

    push();
    rectMode(CENTER);
    angleMode(DEGREES);

    fill(this.color);
    strokeWeight(0);
    rect(this.x *5 - this.width/2, this.y * 5, this.sideLength, this.sideLength);

    pop();
  }
}

function dataDraw(img) {
  img.loadPixels();

  for (let y = 0; y < img.height; y+=3) {
      for (let x = 0; x < img.width; x+=3) {


        const c = getQuick(img, x, y)[0];
        if (c === 0) {
          var currentSquare = new Square(x,y, img.width, img.height);

          currentSquare.draw();

        }

        setQuick(img, x, y, c);
      }
    }
  img.updatePixels();
  }

function getQuick(img, x, y) {
  const i = (y * img.width + x) * 4;
  return [
    img.pixels[i],
    img.pixels[i + 1],
    img.pixels[i + 2],
    img.pixels[i + 3],
  ];
}

function setQuick(img, x, y, c) {
  const i = (y * img.width + x) * 4;

  img.pixels[i + 0] = c[0];
  img.pixels[i + 1] = c[1];
  img.pixels[i + 2] = c[2];
  img.pixels[i + 3] = c[3];
}

