floorheight = 4;
wallwidth = 0.25;
middleroom = 3.5;
turretsize = 0.5;
windowsize = 1.2;
windowjamb = 0.15;
middlewindowheight = 5;
outerwallsize = middleroom*3;
towerheight = floorheight*2;
towersize = 1;
doorsize = 2;

///////////////////////////////////

//WINDOW

module window () {
    difference () {
        //window outer shell
        union () {
            cube([windowsize, wallwidth*2, windowsize], true);

            rotate([90, 0, 0])
            translate([0,windowsize/2,0])
            cylinder($fn = 30, h=wallwidth*2, r=windowsize/2, center=true); 
        }

        //window inner shell, cutout
        union () {
            translate([0, 0, windowjamb/2])
            cube([windowsize - windowjamb*2, wallwidth*2 + 1, windowsize - windowjamb*2 + windowjamb], true);

            rotate([90, 0, 0])
            translate([0,windowsize/2,0])
            cylinder($fn = 30, h=wallwidth*2 +1, r=windowsize/2 - windowjamb, center=true); 
        }
    }

    //window bars
    for (i=[-windowsize/6:windowsize/3:windowsize/6]) {
        translate([i,0,windowjamb*2])
        cylinder($fn = 50, h=windowsize*1.3, r=windowjamb/2, center=true);
    }
}

//window shell
module window_outer () {
    union () {
        cube([windowsize, wallwidth*2, windowsize], true);

        rotate([90, 0, 0])
        translate([0,windowsize/2,0])
        cylinder($fn = 30, h=wallwidth*2, r=windowsize/2, center=true); 
    }
}

///////////////////////////////////

//MIDDLE TOWER

module middle_tower () {
    //tower with inner cutout
    translate ([0, 0, floorheight]) 
    difference () {
        cube([middleroom, middleroom , floorheight*2], true);
        cube([middleroom - (wallwidth*2), middleroom - (wallwidth*2) , floorheight*2 + 1], true);
    }
}

module middle_top () {
    //inverted cut pyramid shape
    translate ([0, 0, floorheight*2])
    rotate ([0, 0, 45])
    cylinder(wallwidth*3, middleroom*0.71,middleroom,$fn=4); //z, bottom, top

    //roof parapet
    translate ([0, 0, floorheight*2 + wallwidth*3 + wallwidth*2.5]) {
        difference () {
            cube([middleroom*1.45, middleroom*1.45 , wallwidth*5], true);
            cube([middleroom*1.45 - (wallwidth*2), middleroom*1.45 - (wallwidth*2) , floorheight*2 + 1], true);
        }
    }
}

module middle_turret_cut () {
    union () {
        for (i=[-1.5:turretsize*2:1.5]) {
            //y axis turret cutouts
            translate([0,i,floorheight*2 + wallwidth*3 + wallwidth*5 - turretsize/2])
            cube([10,turretsize,turretsize*1.1], true);

            //y axis turret cutouts
            translate([i,0,floorheight*2 + wallwidth*3 + wallwidth*5 - turretsize/2]) 
            cube([turretsize,10,turretsize*1.1], true);
        }
    }
}

module middle_roof () {
    difference () {
        middle_top ();
        middle_turret_cut ();
    }
}

module middle_window_cutouts () {
    union () {
        translate([0,middleroom/2 - wallwidth/2,middlewindowheight])
        window_outer ();

        translate([0,-middleroom/2 + wallwidth/2,middlewindowheight])
        window_outer ();

        rotate([0,0,90]) {
            translate([0,middleroom/2 - wallwidth/2,middlewindowheight])
            window_outer ();

            translate([0,-middleroom/2 + wallwidth/2,middlewindowheight])
            window_outer ();
        }
    }
}

module middle_windows () {
    translate([0,middleroom/2 - wallwidth/2,middlewindowheight])
    window ();

    translate([0,-middleroom/2 + wallwidth/2,middlewindowheight])
    window ();

    rotate([0,0,90]) {
        translate([0,middleroom/2 - wallwidth/2,middlewindowheight])
        window ();

        translate([0,-middleroom/2 + wallwidth/2,middlewindowheight])
        window ();
    }
}

///////////////////////////////////

//OUTER WALL

module outer_wall () {
    //wall with inner cutout
    translate ([0, 0, floorheight/2]) 
    difference () {
        cube([outerwallsize, outerwallsize , floorheight], true);
        cube([outerwallsize - (wallwidth*2), outerwallsize - (wallwidth*2) , floorheight + 1], true);
    }
}

module outer_turret_cut () {
    union () {
        for (i=[-4.5:turretsize*2:4.5]) {
            //y axis turret cutouts
            translate([0,i,floorheight - turretsize/2])
            cube([15,turretsize,turretsize*1.1], true);

            //y axis turret cutouts
            translate([i,0,floorheight - turretsize/2]) 
            cube([turretsize,15,turretsize*1.1], true);
        }
    }
}

