//OUTER TOWERS

module outer_tower_structure () {   
    //tower
    difference () {
        cylinder($fn=75, h=towerheight, r=towersize, center=true);
        cylinder($fn=75, h=towerheight + 1, r=towersize - wallwidth, center=true);
    }

    //roof
    translate ([0, 0, towerheight/2])
    cylinder(towerheight*0.4, towersize*1.75, 0, $fn=100); 
}

module outer_tower () {
    //cutting out window
    color("grey") difference () {
        translate ([0, 0, towerheight/2])
        outer_tower_structure ();

        translate ([0, towersize*0.8, towerheight*0.7])
        window_outer ();
