    //adding window
    translate ([0, towersize*0.8, towerheight*0.7])
    color([140/255, 105/255, 30/255])
    window ();
}

//drawing all windows
module all_towers () {
    translate ([outerwallsize/2, outerwallsize/2, 0])
    outer_tower ();

    translate ([-outerwallsize/2, outerwallsize/2, 0])
    outer_tower ();

    translate ([-outerwallsize/2, -outerwallsize/2, 0])
    rotate([0, 0, 180])
    outer_tower ();

    translate ([outerwallsize/2, -outerwallsize/2, 0])
    rotate([0, 0, 180])
    outer_tower ();
}

