    let a;
    if (random()<0.5)
        {a = min(random(-7, 7), random(-7, 7), random(-7, 7), random(-7, 7));}
    else
        {a = max(random(-ballSpeed, ballSpeed), random(-ballSpeed,ballSpeed), random(-ballSpeed, ballSpeed), random(-ballSpeed, ballSpeed));}


    return a;
  }


