// require https://cdnjs.cloudflare.com/ajax/libs/p5.js/0.5.14/p5.js

let num = 10;
let eMax = 30;
let eMin = 5;
let colHue = 285;

function setup() {
  createCanvas(400, 400);
  angleMode(DEGREES);
  noStroke();
  frameRate(3);
  colorMode(HSB)
}

function draw() {
  background(220);

  for (let i = 2 * num; i < width; i += 4 * num) {
    for (let j = 2 * num; j < height; j += 4 * num) {

      let inner;
      let outer;
      let d = dist(i, j, mouseX, mouseY);
      let c = map(d, 0, width, 100, 0);

      if (d < width / 4) {
        inner = min(
          random(eMin),
          random(eMin),
          random(eMin),
          random(eMin),
          random(eMin)
        );
        outer = min(
          random(eMax),
          random(eMax),
          random(eMax),
          random(eMax),
          random(eMax)
        );
      } else if (d < width / 2) {
        inner =
          (random(eMin) +
            random(eMin) +
            random(eMin) +
            random(eMin) +
            random(eMin)) /
          5;
        outer =
          (random(eMax) +
            random(eMax) +
            random(eMax) +
            random(eMax) +
            random(eMax)) /
          5;
      } else {
        inner = max(
          random(eMin),
          random(eMin),
          random(eMin),
          random(eMin),
          random(eMin)
        );
        outer = max(
          random(eMax),
          random(eMax),
          random(eMax),
          random(eMax),
          random(eMax)
        );
      }

      push();

      translate(i, j);

      for (let a = 0; a < 8; a++) {
        rotate((a * 360) / 8);
        fill(colHue, c, 100);
        beginShape();
        curveVertex(0, 0);
        curveVertex(inner, 0);
        curveVertex(outer + inner, outer - inner);
        curveVertex(outer + inner, outer + inner);
        curveVertex(outer - inner, outer + inner);
        curveVertex(0, inner);
        curveVertex(0, 0);
        endShape();
      }
      fill(colHue, c, 50);
      ellipse(0, 0, 2*inner, 2*inner);

      pop();

    }
  }

}

// require https://cdnjs.cloudflare.com/ajax/libs/tone/13.8.6/Tone.min.js
/* global Tone */

const synth = new Tone.PolySynth(11, Tone.Synth).toMaster();
const synth2 = new Tone.PolySynth(11, Tone.Synth).toMaster();

const chord1 = {
    notes:["B4", "G4", "E4", "C4"],
    length: 2
}

const chord2 = {
    notes:["C5", "A4", "F4", "D4"],
    length: 2
}

const chord3 = {
    notes:["A4", "G4", "C4", "D#4"],
    length: 1
}

const chord4 = {
    notes:["G4", "C4", "D#4", "G#3",],
    length: 1
}

const chordProg = [chord1, chord2, chord1, chord3, chord4, chord1, chord2, chord1, chord3, chord4];
window.onmousedown = () => {

  let t = Tone.now();
  for (const chord of chordProg) {
    if (chord.notes !== "rest") {
      synth.triggerAttackRelease(chord.notes, Tone.Time(chord.length), t);
    }
    t += Tone.Time(chord.length);
  }
};

