  font = loadFont('dogicapixelbold.otf');

  let playerImg = loadImage('player.png');
  let boundaryImg = loadImage('rock.png');

  blocker = createSprite(width/2, height/2, playerwidth, playerwidth);
  blocker.addAnimation(
    'normal', 
    'broccoli.png'
  );
  blocker.scale = 0.15;

  player = createSprite(width-60, 50, playerwidth, playerwidth);
  player.addImage('main', playerImg);
  player.scale = playerwidth/483;
  player.mirrorX(-1);

  rocknumwidth = 14;
  rocknumheight = 18;
  rockwidth = width/rocknumwidth;
  rockheight = 158*rockwidth/295;
  for (let i=0; i<rocknumwidth; i++) {
    bottomwall[i] = createSprite((rockwidth/2) + i*rockwidth, height - rockheight/2, rockwidth, rockheight);
    bottomwall[i].addImage('main', boundaryImg);
    bottomwall[i].scale = rockwidth/295;
    bottomwall[i].immovable = true;
    topwall[i] = createSprite((rockwidth/2) + i*rockwidth, rockheight/2, rockwidth, rockheight);
    topwall[i].addImage('main', boundaryImg);
    topwall[i].scale = rockwidth/295;
    topwall[i].immovable = true;
  }
  for (let i=0; i<rocknumheight; i++) {
    rightwall[i] = createSprite(width - rockwidth/2, (rockheight/2) + i*rockheight, rockwidth, rockheight);
    rightwall[i].addImage('main', boundaryImg);
    rightwall[i].scale = rockwidth/295;
    rightwall[i].immovable = true;
    leftwall[i] = createSprite(rockwidth/2, (rockheight/2) + i*rockheight, rockwidth, rockheight);
    leftwall[i].addImage('main', boundaryImg);
    leftwall[i].scale = rockwidth/295;
    leftwall[i].immovable = true;
  }

  let icecreamImg = loadImage('icecream.png');
  let leftheightfactor = (height- 2*rockheight)/3;
  playerdoor = createSprite(wallwidth*13, (height/2) + leftheightfactor + 15, wallwidth, leftheightfactor);
  playerdoor.addImage('main', icecreamImg);
  playerdoor.scale = 0.09;

  fire = new Group();

  for (let i=0; i<5; i++) {
    fires = createSprite(random(rockwidth+50+playerwidth, width-rockwidth-50-playerwidth), random(rockheight+100, height-rockheight-100), wallwidth, leftheightfactor);
    fires.addAnimation('normal', 'fire1.png', 'fire2.png', 'fire2.png', 'fire1.png');
    fire.add(fires);
  }

}

function draw() {
  switch(mode){
    case 0:
      gameplay();
      break;
    case 1:
      playerwin();
      break;
    case 2:
      playerlose();
      break;
  }
}

function gameplay() {
  background(212,197,226);
  drawSprites();

  push();
    fill(154, 137, 171);
    textFont(font);
    textSize(30);
    text('BROCC-ATTACK', width/2, 60);
  pop();

  if(keyIsDown(65)){
    blocker.position.x-=gamespeed;
    blocker.mirrorX(-1);
  }
  if(keyIsDown(87)){
    blocker.position.y-=gamespeed;
  }
  if(keyIsDown(68)){
    blocker.position.x+=gamespeed;
    blocker.mirrorX(1);
  }
  if(keyIsDown(83)){
    blocker.position.y+=gamespeed;
  }

  if(keyIsDown(37)){
    player.position.x-=gamespeed;
    player.mirrorX(-1);
  }
  if(keyIsDown(38)){
    player.position.y-=gamespeed;
  }
  if(keyIsDown(39)){
    player.position.x+=gamespeed
    player.mirrorX(1);
  }
  if(keyIsDown(40)){
    player.position.y+=gamespeed;
  }

  for (let i=0; i<rocknumheight; i++) {
    blocker.collide(rightwall[i]);
    player.collide(rightwall[i]);
    blocker.collide(leftwall[i]);
    player.collide(leftwall[i]);
  }
  for (let i=0; i<rocknumwidth; i++) {
    blocker.collide(topwall[i]);
    player.collide(topwall[i]);
    blocker.collide(bottomwall[i]);
    player.collide(bottomwall[i]);
  }

  // player.displace(blocker);
  blocker.displace(player);

  blocker.collide(playerdoor);
  blocker.collide(fire);
  fire.collide(fire);

  if (player.collide(playerdoor)) {
    mode=1;
  }
  if (player.collide(fires)) {
    mode=2;
  }
}

function playerwin() {
  background(0);
  text("yay! player escaped the burning flames", width/2, height/2 - 8);
  text("press A to play again", width/2, height/2 + 8);
  if(keyIsDown(65)){
    window.location.reload();
  }
}

function playerlose() {
  background(0);
  text("oops. player was engulfed in fire", width/2, height/2 - 8);
  text("press A to play again", width/2, height/2 + 8);
  if(keyIsDown(65)){
    window.location.reload();
  }
}

// require https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js
// Dot Challenge Starting Point

function setup() {
  createCanvas(400, 400);
  colorMode(HSB);
  noStroke();
  frameRate(1);
}

function draw() {
  noiseSeed(frameCount);
  drawPattern();
  //noLoop();
}

function drawPattern() {
  ellipseMode(CENTER);
  background("rgb(246,246,246)");
  let noiseFrequency = 0.02;

  for (let i = 0; i < 120; i++) 
  {
    // POSITION
    let x = noise(i*10, 0)*(width);
    let y = noise(i*40, 5)*(height);

    // DIAMETER
    let diameterNoise = max(noise(i, 1), noise(i, 5));
    let diameter = diameterNoise*i/4.5 + 6;

    // COLOUR
    let hue = diameter*15;
    fill(hue, 50, 100);

    // DRAW 
    ellipse(x, y, diameter, diameter);
    ellipse(width-x, y, diameter, diameter);
    ellipse(x, height-y, diameter, diameter);
    ellipse(width-x, height-y, diameter, diameter);
    ellipse(y, x, diameter, diameter);
    ellipse(y, height-x, diameter, diameter);
    ellipse(width-y, x, diameter, diameter);
    ellipse(width-y, height-x, diameter, diameter);

  }
}

function mouseClicked() {
  noiseSeed(random(200));
  drawPattern();
}

