let t = 0;
let x = 0;
let y = 0;
let heartRate = 60; // in beats per minute

function preload () {
  heart = loadImage('heart.png');
}

function setup() {
  createCanvas(400, 400);
  background(0);
  stroke(0);
  fill(255);
}

function draw() {
  background(0);
  image(heart, 0, 0, 400, 400);
  stroke(255);
  noFill();
  beginShape();
  for (let i = 0; i < width; i++) {
    let noiseVal = noise(t) * height/2;
    let y = map(noiseVal, 0, height/2, height/2, 0);
    vertex(i, y);
    t += 0.01;
  }
  endShape();

  stroke(255);
  fill(255);
  let heartRateInSecs = 60 / heartRate;
  let beatTime = map(sin(frameCount / (heartRateInSecs * 60)), -1, 1, 0, 1);
  x = beatTime * width;
  y = map(noise(beatTime), 0, 1, height/2, 0);
  ellipse(x, y, 20, 20);

  // Draw the lines of the graph
  stroke(255, 50);
  for (let i = 0; i < width; i += 10) {
    let noiseVal = noise(t + i / 100) * height/2;
    let y1 = map(noiseVal, 0, height/2, height/2, 0);
    let y2 = map(noiseVal, 0, height/2, height/2, height);
    line(i, y1, i, y2);
  }
}

using System.Collections;
using System.Collections.Generic;
using UnityEngine;

public class CubeNoise : MonoBehaviour
{
    private float timer;
    void Start()
    {
        timer = 0f; 
    }

    void Update()
    {
        int childCounter = 0;
        timer += Time.deltaTime;

        foreach (Transform child in this.transform)
        {
            GameObject obj = child.gameObject;
            childCounter++;

            float angX = Mathf.PerlinNoise(timer * 0.33f, childCounter * 0.1f) * 90;
            Vector3 newRotation = new(angX, angX, angX);
            obj.transform.eulerAngles = newRotation;

        }
    }
}

