// require https://cdnjs.cloudflare.com/ajax/libs/p5.js/0.5.14/p5.js

let particles = [];
let numParticles = 50;
let gravity = 0.5;
let repulsion = 50;
let noiseScale = 0.02;

function setup() {
  createCanvas(400, 400);
  for (let i = 0; i < numParticles; i++) {
    particles.push(new Particle(random(width), random(height)));
  }
}

function draw() {
  background(0);
  for (let i = 0; i < particles.length; i++) {
    particles[i].update();
    particles[i].display();
    particles[i].attract(particles);
    particles[i].repel(particles);
  }
}

class Particle {
  constructor(x, y) {
    this.pos = createVector(x, y);
    this.vel = createVector(0, 0);
    this.acc = createVector(0, 0);
    this.size = random(5, 10);
    this.color = color(random(255), random(255), random(255));
    this.noiseOffset = createVector(random(1000), random(1000));
  }

  update() {
    let noiseVal = noise(this.noiseOffset.x, this.noiseOffset.y);
    let angle = map(noiseVal, 0, 1, 0, TWO_PI);
    let mag = map(noiseVal, 0, 1, 0, 5);
    let noiseVec = p5.Vector.fromAngle(angle);
    noiseVec.setMag(mag);
    this.acc.add(noiseVec);
    this.vel.add(this.acc);
    this.vel.limit(5);
    this.pos.add(this.vel);
    this.acc.mult(0);
    if (this.pos.x < 0) {
      this.pos.x = width;
    }
    if (this.pos.x > width) {
      this.pos.x = 0;
    }
    if (this.pos.y < 0) {
      this.pos.y = height;
    }
    if (this.pos.y > height) {
      this.pos.y = 0;
    }
    this.noiseOffset.add(this.vel.copy().mult(noiseScale));
  }

  display() {
    noStroke();
    fill(this.color);
    ellipse(this.pos.x, this.pos.y, this.size, this.size);
  }

  attract(particles) {
    for (let i = 0; i < particles.length; i++) {
      if (particles[i] !== this) {
        let force = p5.Vector.sub(particles[i].pos, this.pos);
        let distanceSq = force.magSq();
        if (distanceSq < 40000) {
          force.setMag(gravity);
          this.acc.add(force);
        }
      }
    }
  }

  repel(particles) {
    for (let i = 0; i < particles.length; i++) {
      if (particles[i] !== this) {
        let force = p5.Vector.sub(this.pos, particles[i].pos);
        let distanceSq = force.magSq();
        if (distanceSq < 1000) {
          force.setMag(repulsion);
          this.acc.add(force);
        }
      }
    }
  }
}

