let poseNet;
let poses = [];
let video;

let rot = 0;
const noiseAmp = 50;

function setup() {
  createCanvas(600, 600);
  angleMode(DEGREES);
  strokeWeight(2);

  video = createCapture(VIDEO);
  video.size(width, height);
  poseNet = ml5.poseNet(video, {multiplier:0.5}, modelReady);
  poseNet.on('pose', function(results) {
    poses = results;
  });
}

function modelReady() {
  select('#status').html('Model Loaded');
}

function draw() {
  background(240);

  let imgsize = 80;
  image(video, 0, 0, imgsize*1.2, imgsize);

  let sizeFactor;
  let angleFactor;
  let mapFactor;  
  for (let i = 0; i < poses.length; i++) {
    let skeleton = poses[i].skeleton;
    for (let j = 0; j < skeleton.length; j++) {
      mapFactor = skeleton[0][1]
      sizeFactor = map(mapFactor.position.x,0,width,75,400)
      angleFactor = map(mapFactor.position.y,0,width,1,4)
    }
  }

  noStroke();
  fill("rgba(218,38,38,0.42)");
  //noFill();
  drawSpiral(width / 2, height / 2, sizeFactor, angleFactor, 0);
  let flag = 2
  rot += flag;
  if (rot >= 360) rot=0;

}

function drawSpiral(x, y, radiusMax, angleFactor) {
  push();
  translate(x, y);
  rotate(rot);
  scale(1.2);
  for (
    let angle = 0, radius = 0;
    radius < radiusMax;
    angle += noise(radius) * angleFactor
  ) {
    ellipse(sin(angle) * radius, cos(angle) * radius, 5);
    radius +=
      noise(sin(angle) * radius * noiseAmp, cos(angle) * radius * noiseAmp) *
      0.1;
    if (angle > 360) angle = 0;
  }
  pop();
}

