window.onload = function () {
    // Get a reference to the canvas object
    const canvas = document.getElementById("myCanvas");
    // Create an empty project and a view for the canvas:
    paper.setup(canvas);

    const paths = [];
    const rectPaths = [];
    const rects = [];

    for (let i = 0; i < 200; i++) {
        // Create a Paper.js Path to draw a line into it:
        paths.push(new paper.Path());
        rects.push(new paper.Point(i*10, i*12+10), new paper.Size(500, i*20));
        rectPaths.push(new paper.Path.Rectangle(rects[i]));
        // Give the stroke a color
        paths[i].strokeColor = "#702099";
        rectPaths[i].strokeColor = "#209907";

        const start = new paper.Point(i*2, i*2 +100);

        // Move to start and draw a line from there
        paths[i].moveTo(start);

        // Note that the plus operator on Point objects does not work
        // in JavaScript. Instead, we need to call the add() function:
        paths[i].lineTo(start.add([i*12 - 200, i*2 - 300]));
    }

    // Draw the view now:
    paper.view.draw();
};

