  createCanvas(1000, canvasHeight);
  noLoop();
  colorMode(HSL);
}

function draw() {
  background(0);
  let x = 60;
  let squareY = 60;
  let triY = canvasHeight - 60;
  let triLength = 100;
  noStroke();
  blendMode(DIFFERENCE)
  for (let i = 0; i < 12; i++) {
    let h = noise(x+i)*30+00;
    let s = noise(x+i)*100+20;
    let l = noise(x+i)*100+10;
    fill(h, s, l);
    square(x+i*65, squareY, 100+(noise(x+i)*280));
  }

  for (let i = 0; i < 24; i++) {
    let h = noise(x+i)*30+00;
    let s = noise(x+i)*100+20;
    let l = noise(x+i)*100+10;
    fill(h, s, l);
    triangle(
      x+i*35, 
      triY, 
      x + i * 35 + (triLength-noise(x+i)*40+20), 
      triY, 
      x + i * 35 + (triLength/2-noise(x+i)*40+20),
      triY-180-noise(x+i)*280
    );
  }
}

// require https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.min.js

let circles = [];
const numCircles = 8;
const radius = 40;
const spacing = 75;
const easing = 0.02;

let r = 0;
let theta = 0;
let inc = 0.02;

let colours = [
  "red",
  "green",
  "blue",
  "yellow",
  "purple",
  "orange",
  "cyan",
  "magenta",
];

function setup() {
  createCanvas(windowWidth, windowHeight);
  noStroke();
  for (let i = 0; i < numCircles; i++) {
    const x = mouseX + spacing * (i - 2);
    const y = mouseY;
    circles.push({ x, y });
  }
}

function draw() {
  background(230);
  for (let i = 0; i < numCircles; i++) {
    let circle = circles[i];
    const dx = mouseX - circle.x;
    const dy = mouseY - circle.y;
    circle.x += dx * easing;
    circle.y += dy * easing;

    r = 50;
    let a = r * cos(theta);
    let b = r * sin(theta);
    theta = theta += inc;

    // let ex = noise(i * dx * 5) * 100;
    // let ey = noise(i * dy * 5) * 100;
    let ex = noise(i) * dx * 5;
    let ey = noise(i) * dy * 5;

    fill(colours[i]);
    ellipse(circle.x + ex + a, circle.y + ey + b, radius);
  }
}

