let worldImage;

function preload() {
  worldImage = loadImage("grad.jpg");
}

function setup() {
  createCanvas(500, 500);
  pixelDensity(1);
}

function draw() {
  background(0);

  worldImage.loadPixels();

  for (let y = 0; y < worldImage.height; y++) {
    for (let x = 0; x < worldImage.width; x++) {
      const in_color = worldImage.get(x, y);

      const r = red(in_color);
      const g = green(in_color);
       const b = blue(in_color);

      let out_color;
      if (r < 120) {
        out_color = color("pink");
      } else if (g < 200 && b >130) {
        out_color = color("teal");
      }
      // else{
      //   out_color = color("#34E3EB");
      // }

      worldImage.set(x, y, out_color);
    }
  }

  worldImage.updatePixels();

  noSmooth();
  image(worldImage, 0, 0, width, height);

  noLoop();
}

// require https://cdnjs.cloudflare.com/ajax/libs/tone/13.8.6/Tone.min.js
// require https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js
//partly adapted from https://compform.net/js_lab/js_lab.html?/music/tone_sketches/synth.js

let canvasWidth = 500;
let canvasHeight = 500;

let ellipse0 = {
    xoffset: 0,
    yoffset: 0,
    xPos: 0,
    yPos: 0,
    esize: 20
};
let amplitude;
let vibration = 25;
let string = [
    {deceleration: 0, stringX: canvasWidth/2 - 100, stringYoffset: 150, bezierYoffset: 50, note: "C4"},
    {deceleration: 0, stringX: canvasWidth/2 - 50, stringYoffset: 100, bezierYoffset: 75, note: "F4"},
    {deceleration: 0, stringX: canvasWidth/2, stringYoffset: 50, bezierYoffset: 100, note: "B4"},
    {deceleration: 0, stringX: canvasWidth/2 + 50, stringYoffset: 100, bezierYoffset: 75, note: "F4"},
    {deceleration: 0, stringX: canvasWidth/2 + 100, stringYoffset: 150, bezierYoffset: 50, note: "C4"},
  ];

const synth = new Tone.Synth(
  {
    oscillator: {
      type: "sine",
    },
    envelope: {
      attack: 0.1,
      decay: 0.1,
      sustain: 0.5,
      release: 1,
    },
  }
);
synth.toMaster();

function setup() {
  createCanvas(canvasWidth, canvasHeight);
  colorMode(HSB);
}

function draw() {
  background(250, 50, 85);

  ellipse0.xPos = map(noise(ellipse0.xoffset), 0, 1, 0, width);
  ellipse0.yPos = map(noise(ellipse0.yoffset), 0, 1, 0, height);

  fill(0, 0, 100);
  noStroke();
  ellipse(ellipse0.xPos, ellipse0.yPos, ellipse0.esize);

  ellipse0.xoffset+=0.01;
  ellipse0.yoffset+=0.001;

  for (let i=0; i<string.length; i++) {

    amplitude = (vibration/2) * sin(TWO_PI * (millis()/1000)) * string[i].deceleration;

    noFill();
    stroke(0, 0, 100);
    strokeWeight(1.75);
    bezier(
      string[i].stringX, string[i].stringYoffset,
      amplitude + string[i].stringX, string[i].stringYoffset + string[i].bezierYoffset, 
      amplitude + string[i].stringX, height - string[i].stringYoffset - string[i].bezierYoffset, 
      string[i].stringX, height - string[i].stringYoffset
    );

    string[i].deceleration -= 0.01;
    if (string[i].deceleration < 0) {
      string[i].deceleration = 0;
    }

    if (
        ellipse0.xPos > string[i].stringX - 5 && 
        ellipse0.xPos < string[i].stringX + 5 && 
        ellipse0.yPos > string[i].stringYoffset && 
        ellipse0.yPos < height - string[i].stringYoffset
    ) {
      synth.triggerAttackRelease(string[i].note, "4n");
      string[i].deceleration = 1;
    }

  }

};

