let lego = [];
let legoX = [0,50,100,150,200,250,300];
let legoY = 0;
let legoNum = legoX.length*8;

function setup() {
  createCanvas(400, 400);
  for (let i=0; i<legoNum; i++) {
    lego[i] = new Lego(random(legoX),legoY-(400*i));
  }
}

function draw() {
  background(220);
  let colour = [color('red'),color('yellow'),color('green'),color('blue')];
  for (let i=0; i<legoNum; i++) {
    lego[i].drawLego(colour[i%4]);
    lego[i].moveLego();
    lego[i].collision(lego);
    lego[i].reloadScreen();
  }
}

class Lego {
  constructor(LegoX, LegoY) {
    this.x = LegoX;
    this.y = LegoY;
    this.legoWidth = 100;
    this.speed = 3;
  }
  drawLego(LegoColour) {
    noStroke();
    fill(LegoColour);
    rect(this.x, this.y, this.legoWidth, this.legoWidth/2);
    rect(this.x+(this.legoWidth/8),this.y-(this.legoWidth/8),this.legoWidth/4,this.legoWidth/8);
    rect(this.x+((5*this.legoWidth)/8),this.y-(this.legoWidth/8),this.legoWidth/4,this.legoWidth/8);
  }
  moveLego() {
    this.y+=this.speed;
    if (this.y > (height-(this.legoWidth/2))) {
      this.speed=0;
    }
  }
  collision(array) {
    for (let i=0;i<legoNum;i++) {
      if ( ((array[i].x-this.x)>=0) && ((array[i].x-this.x)<70) && ((array[i].y-this.y)>0) && ((array[i].y-this.y)<50)  ) {
        this.speed = 0;
      }
    }
  }
  reloadScreen() {
    if(this.y>0 && this.y<=50 && (this.speed<0.5)) {
      window.location.reload();
    }
  }
}

