function scoreUp(side) {
  if (side === 'left') {
    rightScore++
    resetGame()
  } else {
    leftScore++
    resetGame()
  }

}

function serve() {
  ball.position.x = 400;
  ball.position.y = 400;

  let dir = Math.random();
  let ang = (Math.random() - 0.5) * 60 + 10
  if (dir <= 0.5) {
    ball.setSpeed(10, ang )
  } else {
    ball.setSpeed(-10, ang)
  }

}

function resetGame() {
