  placePointsCullRange(50, width, height, 20, 100)

  for (let i = 0; i < points.length; i++) {
    // pull the coordianates from the current point in array
    let x = points[i].x;
    let y = points[i].y;

    let n=noise(i*0.1) *60 - 30;

       let colorPick = noise(i) 
       colorPick = map(colorPick, 0, 1, 90, 200)

    // the colors also need to change
    // what colorMode would be easiest to work with?
    colorMode(HSL, 360, 100, 100, 1)
    // noFill()
    stroke(colorPick, 100, 50);

    fill(colorPick, 100, 50, noise(i)-0.5)

    rect(x, y, 50 - n, 50 + n);
  }

  // noLoop();
}

function placePointsCullRange(count, w, h, min_distance, max_distance) {

  let current_try = 0;
  let max_tries = count * 10;

  // init with one point in center
  points.push({
    x: w * 0.5,
    y: h * 0.5,
  });

  while (points.length < count && current_try < max_tries) {
    current_try++;

    let x = random() * w;
    let y = random() * h;

    let isTooClose = checkClose(x, y, points, min_distance);
    let isCloseEnough = checkClose(x, y, points, max_distance);

    if (!isTooClose && isCloseEnough) {
      points.push({
        x: x,
        y: y,
      });
    }
  }

  return points;
}

function checkClose(x, y, points, min_distance) {
  for (let i = 0; i < points.length; i++) {
    if (dist(points[i].x, points[i].y, x, y) < min_distance) {
      return true;
    }
  }
  return false;
}

