// Define the parameters of the fractal shape
DEPTH = 4;
THETA = 60;
SCALE = 0.7;

// Define the recursive function to create the fractal shape
module fractal(depth, size, color1, color2, color3) {
  if (depth > 0) {
    color(color1) cube(size = size, center = true);
    translate([0, 0, size/2]) {
      rotate([0, 0, THETA]) {
        color(color2) fractal(depth - 1, size * SCALE, color1, color2, color3);
      }
    }
    translate([0, 0, -size/2]) {
      rotate([0, 0, -THETA]) {
        color(color3) fractal(depth - 1, size * SCALE, color1, color2, color3);
      }
    }
  }
}

