class Point {
  constructor(x, y, rectangle_size) {
    this.x = x;
    this.y = y;
    this.rectangle_size = rectangle_size;
  }
  show() {
    rect(this.x, this.y, this.rectangle_size, this.rectangle_size);
  }
}

class Student {
  constructor() {
    this.x = random(0, width);
    this.y = random(0, height);
    // Each Student will have a random positive or negative direction assigned.
    this.direction_1 = random(-2, 2);
    this.direction_2 = random(-2, 2);
    this.d;
  }

  show() {
    push();
    fill("red");
    ellipse(this.x, this.y, 10, 10);
    pop();
  }

  move() {
    if (res.length % 50 == 0) {
      if (this.direction_1 > 0) {
        this.direction_1 += 1;
      }
      if (this.direction_1 < 0) {
        this.direction_1 -= 1;
      }

      if (this.direction_2 > 0) {
        this.direction_2 += 1;
      }
      if (this.direction_2 < 0) {
        this.direction_2 -= 1;
      }
    }

    this.x = this.x + this.direction_1;
    this.y = this.y + this.direction_2;

    if (this.x > width || this.x < 0) {
      this.direction_1 = -this.direction_1;
    }
    if (this.y > height || this.y < 0) {
      this.direction_2 = -this.direction_2;
    }
  }

  collision_detection() {
    for (let i = 0; i < res.length; i++) {
      this.d = dist(res[i].x, res[i].y, this.x, this.y);
      if (this.d < 5) {
        this.direction_1 = -this.direction_1 + 1;
        this.direction_2 = -this.direction_2 + 1;
        res[i].rectangle_size = res[i].rectangle_size + 2;
      }
    }

    // detecting collision with "OUR"
    for (let i = 0; i < our.length; i++) {
      this.d = dist(our[i].x, our[i].y, this.x, this.y);
      if (this.d < 5) {
        this.direction_1 = -this.direction_1 + 1;
        this.direction_2 = -this.direction_2 + 1;
        our[i].rectangle_size = our[i].rectangle_size + 2;
      }
    }

    // detecting collision with "CES"
    for (let i = 0; i < ces.length; i++) {
      this.d = dist(ces[i].x, ces[i].y, this.x, this.y);
      if (this.d < 5) {
        this.direction_1 = -this.direction_1 + 1;
        this.direction_2 = -this.direction_2 + 1;
        ces[i].rectangle_size = ces[i].rectangle_size + 2;
      }
    }
  }
}

