function draw() {
  background("rgb(0,0,0)");

  noStroke();

  fill("rgb(218,38,38)");
  drawSpiral(width / 2, height / 2);

  // fill("rgb(38,43,218)");
  // drawSpiral(width / 6, height / 8);

  // fill("rgb(24,176,28)");
  // drawSpiral(width / 1.2, height / 1.4);
}

function drawSpiral(x, y) {
  push();

  for (
    let angle = 0, radius = 0;
    radius < params.Width;
    angle += params.AngleFactor
  ) {

    let xPos = x + sin(angle) * radius;
    let yPos = y + cos(angle) * radius;

    circle(
      xPos + noise(xPos*params.Smoothing) * params.NoiseAmp,
      yPos + noise(yPos*params.Smoothing) * params.NoiseAmp,
      params.StrokeWidth
    );

    radius += params.RadiusFactor;

    if (angle > 360) angle = 0;
  }

  pop();
}

