let myTurtle;

// Define a function to draw a circle
function drawCircle(x, y, r) {
  myTurtle.penUp();
  myTurtle.moveTo(x + r, y);
  myTurtle.penDown();

  for (let i = 0; i < 120; i++) {
    myTurtle.moveForward(2 * Math.PI * r / 120);
    myTurtle.turnRight(3);
  }
}

function setup() {
  createCanvas(500, 500);
  myTurtle = new Turtle();
}

function draw() {
  background(50);

  noFill();
  stroke(255);
  strokeWeight(3);

  // draw the rectangle
  myTurtle.penUp();
  myTurtle.moveTo(100, 100);
  myTurtle.penDown();
  myTurtle.moveForward(300);
  myTurtle.turnRight(90);
  myTurtle.moveForward(200);
  myTurtle.turnRight(90);
  myTurtle.moveForward(300);
  myTurtle.turnRight(90);
  myTurtle.moveForward(200);

  // draw the triangle
  myTurtle.penUp();
  myTurtle.moveTo(100, 100);
  myTurtle.penDown();
  for (let i = 0; i < 3; i++) {
    myTurtle.moveForward(300);
    myTurtle.turnRight(120);
  }

  // draw the circle inside the triangle
  drawCircle(250, 200, 100);

  noLoop();
}

import { Configuration, OpenAIApi } from "openai";

const configuration = new Configuration({
  apiKey: process.env.OPENAI_API_KEY,
});
const openai = new OpenAIApi(configuration);

export default async function (req, res) {
  if (!configuration.apiKey) {
    res.status(500).json({
      error: {
        message: "OpenAI API key not configured, please follow instructions in README.md",
      }
    });
    return;
  }

  const animal = req.body.animal || '';
  if (animal.trim().length === 0) {
    res.status(400).json({
      error: {
        message: "Please enter a valid animal",
      }
    });
    return;
  }

  try {
    const completion = await openai.createCompletion({
      model: "text-davinci-003",
      prompt: generatePrompt(animal),
      temperature: .9,
    });
    res.status(200).json({ result: completion.data.choices[0].text });
  } catch(error) {
    // Consider adjusting the error handling logic for your use case
    if (error.response) {
      console.error(error.response.status, error.response.data);
      res.status(error.response.status).json(error.response.data);
    } else {
      console.error(`Error with OpenAI API request: ${error.message}`);
      res.status(500).json({
        error: {
          message: 'An error occurred during your request.',
        }
      });
    }
  }
}

function generatePrompt(animal) {
  const capitalizedAnimal =
    animal[0].toUpperCase() + animal.slice(1).toLowerCase();
  return `Suggest ideas in the format of "Uber for X"

Uber for Babies: A baby is delivered to you on demand for you to raise at any given point in time.
Uber for Dogs: Have a dog delivered to your front door whenever you want. Pricing varies on how long you have the dog for.
Uber for Dragons: A dragon descends upon you from the sky and burns down your house so that you can claim insurance.
Uber for Criminals: A criminal is delivered for you to rehabilitate on the spot in 10 minutes.
Uber for ${capitalizedAnimal}:`;
}

