function writetext(number) {
  // E-D
  beginShape();
  for (i = 0; i < e_letter.length; i += number) {
    vertex(e_letter[i].x, e_letter[i].y);
    vertex(d_letter[i].x, d_letter[i].y);
  }
  endShape();

  // D-U
  beginShape();
  for (i = 0; i < e_letter.length; i += number) {
    vertex(d_letter[i].x, d_letter[i].y);
    vertex(u_letter[i].x, u_letter[i].y);
  }
  endShape();

  // U-C
  beginShape();
  for (i = 0; i < e_letter.length; i += number) {
    vertex(u_letter[i].x, u_letter[i].y);
    vertex(c_letter[i].x, c_letter[i].y);
  }
  endShape();

  // C-A
  beginShape();
  for (i = 0; i < e_letter.length; i += number) {
    vertex(c_letter[i].x, c_letter[i].y);
    vertex(a_letter[i].x, a_letter[i].y);
  }
  endShape();

  // A-T
  beginShape();
  for (i = 0; i < e_letter.length; i += number) {
    vertex(a_letter[i].x, a_letter[i].y);
    vertex(t_letter[i].x, t_letter[i].y);
  }
  endShape();

  // T-I
  beginShape();
  for (i = 0; i < e_letter.length; i += number) {
    vertex(t_letter[i].x, t_letter[i].y);
    vertex(i_letter[i].x, i_letter[i].y);
  }
  endShape();

  // I-O
  beginShape();
  for (i = 0; i < e_letter.length; i += number) {
    vertex(i_letter[i].x, i_letter[i].y);
    vertex(o_letter[i].x, o_letter[i].y);
  }
  endShape();

  // O-N
  beginShape();
  for (i = 0; i < e_letter.length; i += number) {
    vertex(o_letter[i].x, o_letter[i].y);
    vertex(n_letter[i].x, n_letter[i].y);
  }
  endShape();
}

