  for (let i = 0; i < frames.length; i++) {
    let pos = frames[i].position;
    let img = spritesheet.get(pos.x, pos.y, pos.w, pos.h);
    tiles.push(img);
    sprites[i] = new Sprite(img, 0, 0, 'dynamic');
  }

  sprites = shuffle(sprites);
  sprites[0].position.x = 0+100;
  sprites[0].position.y = 0+100;
  sprites[1].position.x = 200+100;
  sprites[1].position.y = 0+100;
  sprites[2].position.x = 400+100;
  sprites[2].position.y = 0+100;
  sprites[3].position.x = 0+100;
  sprites[3].position.y = 200+100;
  sprites[4].position.x = 200+100;
  sprites[4].position.y = 200+100;
  sprites[5].position.x = 400+100;
  sprites[5].position.y = 200+100;
  sprites[6].position.x = 0+100;
  sprites[6].position.y = 400+100;
  sprites[7].position.x = 200+100;
  sprites[7].position.y = 400+100;
}

function draw() {
  background(0);
  stroke(0);

  for (sprite of sprites) {
    if (mouse.pressing()) {
      if (dist(mouseX, 0, sprite.position.x, 0) < 100 && dist(0, mouseY, 0, sprite.position.y) < 100) {
        sprite.position.x = mouseX;
        sprite.position.y = mouseY;
      }
    }

    sprite.position.x = constrain(sprite.position.x, 100, width-100);
    sprite.position.y = constrain(sprite.position.y, 100, height-100);
    sprite.rotation = 0;
    sprite.stroke = 'black';
  }
}

function shuffle(array) {
  let currentIndex = array.length,  randomIndex;
  while (currentIndex != 0) {
    randomIndex = Math.floor(Math.random() * currentIndex);
    currentIndex--;
    [array[currentIndex], array[randomIndex]] = [
      array[randomIndex], array[currentIndex]];
  }
  return array;
}

