    let offsetX = (noise(i*frequency + frameCount*time_speed))*amplitude*10;
    let offsetY = (noise(i*frequency + frameCount*time_speed))*amplitude*10;

    stroke(255-i*100, 255, 255, 255-i*100);

    push();
    blendMode(OVERLAY);
    rect(x + offsetX, y + offsetY, x, y);
    rect(x + offsetX, y + offsetY, x, y);
    pop();

    noFill();

    blendMode(EXCLUSION);
    stroke(255, 255-i*100, 255, 255-i*100);
    rect(x - offsetX, y - offsetY, 1000, 100);
    rect(x - offsetX, y - offsetY, 1000, 200);

    push();
    blendMode(DIFFERENCE);
    fill(255, 255, 10);
    rect(0, 0, width, height);
    pop();
  }
}

let testImage;
let imagePath  =;
function getQuick(img, x, y) {
  const i = (y * img.width + x) * 4;
  return [
    img.pixels[i],
    img.pixels[i + 1],
    img.pixels[i + 2],
    img.pixels[i + 3],
  ];
}

function setQuick(img, x, y, c) {
  const i = (y * img.width + x) * 4;

  img.pixels[i + 0] = c[0];
  img.pixels[i + 1] = c[1];
  img.pixels[i + 2] = c[2];
  img.pixels[i + 3] = c[3];
}

function preload() {
  testImage = loadImage(`${imagePath}`);
  noLoop();
}

function setup() {
  createCanvas(600,600);
  noLoop();
  noSmooth();
  testImage.loadPixels();

}

function draw() {
    // background(0, 0, 0);
  let noiseScale = .00008;


  testImage.loadPixels();

  for (let x = 0; x < testImage.width; x++) {
    for (let y = 0; y < testImage.height; y++) {
      const c = getQuick(testImage, x, y);

      c[0] = noise(x *y * noiseScale, 3* noiseScale) * 255 + c[0] + 10;
      c[1] = noise((x*y) * noiseScale, 2* noiseScale) * 255 + c[1] + 0;
      c[2] = noise((y*x) * noiseScale, 1* noiseScale) * 255 + c[2] + 0;

      if (c[0] >= 255) {
        c[0] = 0 + (255 - c[0])
      }

       if (c[1] >= 255) {
        c[1] = 0 + (255 - c[1])
      }

             if (c[2] >= 255) {
        c[2] = 0 + (255 - c[2])
      }

      setQuick(testImage, x, y, c)

    }
  }

  testImage.updatePixels();
  image(testImage, 0, 0, 600, 600)
}

