function drawSpiral(x, y, angleFactor, radiusFactor) {
  push();


  for (let angle = 0, radius = 0; radius < params.Width; angle += params.AngleFactor) {
    let xPos = x + sin(angle) * radius;
    let yPos = y + cos(angle) * radius;

    circle(xPos + noise(xPos-x)*10, yPos + noise(yPos-y)*10, params.StrokeWidth);

    radius+=params.RadiusFactor;

    if (angle > 360) angle = 0;
  }

  pop();
}

